import React from 'react';
import {useNavigate} from 'react-router-dom';
import BonVoyageService from "../../Repository/BonVoyageRepository";


const Register = (props) => {

    const navigate = useNavigate();
    const [formData, updateFormData] = React.useState({
        username: "",
        password: "",
        repeatPassword: "",
        name: "/",
        surname: "/",
        role: "ROLE_USER",
    })

    const handleChange = (e) => {
        updateFormData({
            ...formData,
            [e.target.name]: e.target.value.trim()
        })
    }

    const onFormSubmit = (e) => {
        e.preventDefault();
        const username = formData.username;
        const password = formData.password;
        const repeatPassword = formData.repeatPassword;
        const name = formData.name;
        const surname = formData.surname;
        const role = formData.role;
        BonVoyageService.register(username, password, repeatPassword, name, surname, role);
        navigate("/login");
    }

    return (
        <div className="row mt-5 justify-content-center">
            <div className="col-md-5">
                <h4>Register Page</h4>
                <form onSubmit={onFormSubmit}>
                    <div className="form-group">
                        <label htmlFor="username">Username</label>
                        <input type="text"
                               className="form-control"
                               name="username"
                               required
                               placeholder="Enter username"
                               onChange={handleChange}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input type="password"
                               className="form-control"
                               name="password"
                               placeholder="Enter password"
                               required
                               onChange={handleChange}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="repeatPassword">Repeat password</label>
                        <input type="password"
                               className="form-control"
                               name="repeatPassword"
                               placeholder="Enter password again"
                               required
                               onChange={handleChange}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="name">Name</label>
                        <input type="text"
                               className="form-control"
                               name="name"
                               required
                               placeholder="Enter name"
                               onChange={handleChange}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="surname">Surname</label>
                        <input type="text"
                               className="form-control"
                               name="surname"
                               required
                               placeholder="Enter surname"
                               onChange={handleChange}
                        />
                    </div>
                    <div className="form-group">
                        <label>Role</label>
                        <select name="roles" className="form-control" onChange={handleChange}>
                            {props.roles.map((term) =>
                                <option key={term} value={term}>{term}</option>
                            )}
                        </select>
                    </div>
                    <br/>
                    <div className="row">
                        <div className="col-12">
                            <button id="submit" type="submit" className="btn btn-primary w-100">Submit</button>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default Register;
