import React from 'react';
import {useNavigate} from 'react-router-dom';
import BonVoyageService from "../../Repository/BonVoyageRepository";


const Login = (props) => {

    const navigate = useNavigate();
    const [formData, updateFormData] = React.useState({
        username: "",
        password: ""
    })

    const handleChange = (e) => {
        updateFormData({
            ...formData,
            [e.target.name]: e.target.value.trim()
        })
    }

    const onFormSubmit = (e) => {
        e.preventDefault();
        BonVoyageService.login(formData.username, formData.password).then(resp => {
            localStorage.setItem("JWT", resp.data);
            props.onLogin()
            navigate("/vehicles");
        })

    }

    return (
        <form onSubmit={onFormSubmit} className="form-signin">
            <img className="mb-4" src="https://getbootstrap.com/docs/4.0/assets/brand/bootstrap-solid.svg" alt=""
                 width="72" height="72"/>
                <h1 className="h3 mb-3 font-weight-normal">Please sign in</h1>
                <label htmlFor="username" className="sr-only">Email address</label>
                <input type="text" id="username" className="form-control" placeholder="Username" required=""
                       autoFocus="" onChange={handleChange}/>
                    <label htmlFor="password" className="sr-only">Password</label>
                    <input type="password" id="password" className="form-control" placeholder="Password"
                           required="" onChange={handleChange}/>
                        <div className="checkbox mb-3">
                            <label>
                                <input type="checkbox" value="remember-me"/> Remember me
                            </label>
                        </div>
                        <button className="btn btn-lg btn-primary btn-block" type="submit">Sign in</button>
                        <p className="mt-5 mb-3 text-muted">BonVoyage Â© 2022</p>
        </form>
    )
}

export default Login;
